                import java.util.*
                import org.kuali.rice.kew.rule.*

                responsibilityPriority = 0
                responsibilities = new ArrayList()

                def addResponsibilities(ruleExprResult) {
                    println  ruleExprResult.getResponsibilities()

                    if (ruleExprResult.getResponsibilities() == null || ruleExprResult.getResponsibilities().size() == 0) return

                    for (responsibility in ruleExprResult.getResponsibilities()) {
                        responsibility.setPriority(Integer.valueOf(responsibilityPriority))
                        responsibilities.add(responsibility)
                    }
                    // increment responsibilityPriority for next rule expression result responsibilities
                    responsibilityPriority++

                }

                success = false
                if ((r = workflow.invokeRule("KRABizRuleWithMaps1")).isSuccess()) {
                    addResponsibilities(r)

                    if ((r = workflow.invokeRule("KRABizRuleWithMaps2")).isSuccess()) {
                        addResponsibilities(r)
                        success = true
                    }
                }

                return new RuleExpressionResult(rule, success, responsibilities)
